const  Hero=(props)=>{
  
  return <>
        <div className={props.clsName}><img alt="heroimg" src={props.aimg}/></div>
        <div className="des">
        <span>{props.sp}</span>
        <p>{props.par}
        </p>
        <a href="#">Travel Plan</a>
        </div>
          
        </>
}
export default Hero;
